function analyzeList(msg=msg_tmp) {
  let listStr = msg.general_msg_list
  listStr = listStr.replace(/\\/g, "")
  let list = JSON.parse(listStr).list

  list.map((data, index)=>{
    const {
      comm_msg_info, // 公开信息、文本信息
      app_msg_ext_info, // 网页信息
      image_msg_ext_info, // 图片信息
    } = data
    const {content} = data.comm_msg_info

    if (app_msg_ext_info) {
      const {title} = app_msg_ext_info
      console.log(index + 1 + '. ' + title)
      return title
    } else if (image_msg_ext_info) {
      console.log(index + 1 + '. ' + image_msg_ext_info.cdn_url)
      return image_msg_ext_info.cdn_url
    } else {
      console.log(index + 1 + '. ' + content)
      return content
    }
  })
}

